import { useState } from "react";
import React from "react";
import * as motion from "motion/react-client";





export default function Board() {
    const {win, setWin} = useState(false);
    const [xIsNext, setXIsNext] = useState(true);
    const [isReversed, setIsReversed] = useState(false);
    const [squares, setSquares] = useState(Array(9).fill(null));
    const [position, setPosition] = useState(Array(9).fill(null));
    const [history, setHistory] = useState([Array(9).fill(null)]);
    const [bgcolor, setBgcolor] = useState(Array(9).fill('white'));
    let status;

    function Square({ value, onSquareClick, bgcolor, id }) {
        if (xIsNext && value === null && !win) {
            return (<motion.button whileHover={{
                scale: 1.2, rotate: 10, backgroundColor: "#2BB95D",
                transition: { duration: 0.2 },
            }}
                whileTap={{ scale: 0.8 }} className="square" onClick={onSquareClick} style={{ backgroundColor: bgcolor }}>{value}</motion.button>);
        }
        if (!xIsNext && value === null && win === false) {
            return (<motion.button whileHover={{
                scale: 1.2, rotate: 10, backgroundColor: "#c92bb4ff",
                transition: { duration: 0.2 },
            }}
                whileTap={{ scale: 0.8 }} className="square" onClick={onSquareClick} style={{ backgroundColor: bgcolor }}>{value}</motion.button>);
        }
        if (value === 'X' && win === false) {
            return (<button className="square" onClick={onSquareClick} style={{ backgroundColor: "#2BB95D" }}>{value}</button>);
        }
        if (value === 'O' && win === false) {
            return (<button className="square" onClick={onSquareClick} style={{ backgroundColor: "#c92bb4ff" }}>{value}</button>);
        }

        return (<button className="square" onClick={onSquareClick} style={{ backgroundColor: bgcolor }}>{value}</button>);
    }


    function calculateWinner(squares) {
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6]
        ];
        for (let i = 0; i < lines.length; i++) {
            const [a, b, c] = lines[i];
            if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
                bgcolor[a] = 'lightgreen';
                bgcolor[b] = 'lightgreen';
                bgcolor[c] = 'lightgreen';
                setWin(true);
                return squares[a];
            }
        }
        return null;
    }

    function handleClick(i) {
        const nextSquares = squares.slice();
        if (nextSquares[i] || calculateWinner(nextSquares)) return;
        nextSquares[i] = xIsNext ? 'X' : 'O';
        for (let j = 0; j < 9; j++) {
            if (position[j] === null) {
                position[j] = i;
                break;
            }
        }
        console.log(position);
        setHistory([...history, nextSquares]);
        setSquares(nextSquares);
        setXIsNext(!xIsNext);
    }

    function goToMove(nextMove) {
        setSquares(history[nextMove]);
        history.splice(nextMove + 1);
        setXIsNext((nextMove % 2) === 0);
        setBgcolor(Array(9).fill('white'));
    }

    const moves = history.map((history, move) => {
        let description;
        let row = position[move - 1] !== null ? Math.floor(position[move - 1] / 3) + 1 : null;
        let col = position[move - 1] !== null ? (position[move - 1] % 3) + 1 : null;
        if (move > 0) {
            description = "Go to move #" + move + " ( position : " + row + "," + col + " )";
        } else {
            return null;
        }
        return (<li key={move}>
            <button onClick={() => goToMove(move)}>{description}</button>
        </li>);
    })

    const winner = calculateWinner(squares);

    if (winner) {
        status = "Winner : " + winner;
    } else {
        status = "NextPlayer : " + (xIsNext ? 'X' : 'O');
    }

    const atmove = "You are at move #" + (history.length - 1);

    function Reset() {
        setXIsNext(true);
        setSquares(Array(9).fill(null));
        setHistory([Array(9).fill(null)]);
        setBgcolor(Array(9).fill('white'));
        setPosition(Array(9).fill(null));
    }


    const arr = [0, 1, 2, 3, 4, 5, 6, 7, 8];
    const mapped = arr.map((rep) => (
        <React.Fragment key={rep}>
            <Square
                value={squares[rep]}
                onSquareClick={() => handleClick(rep)}
                bgcolor={bgcolor[rep]}
                id ={rep}
            />
        </React.Fragment>
    ));

    const mapRows = [];
    for (let i = 0; i < 3; i++) {
        mapRows.push(
            <div className="board-row" key={i}>
                {mapped.slice(i * 3, i * 3 + 3)}
            </div>
        );
    }

    const reverse = isReversed ? moves.slice().reverse() : moves;

    function toggle() {
        setIsReversed(!isReversed);
    }
    return (
        <div className="game">
            <div className="root">
                <>{mapRows}</>
                <p className="temp status">{status}</p>
                <p className="temp atmove">{atmove}</p>
                <motion.div className="temp" drag dragMomentum={true}>
                    <button className="button" onClick={Reset}>Reset</button>
                </motion.div>
            </div>
            <div className="temp">
                <label class="switch">
                    <input type="checkbox" onChange={toggle}>
                    </input>
                    <span class="slider round"></span>
                </label>
                <ol>
                    {reverse}
                </ol>
            </div>
        </div>
    );
}
